import React, { useEffect, useState } from 'react';
import Header from '../../components/shared/Header';
import { Box, Grid2, TextField } from '@mui/material';
import { useParams } from 'react-router-dom';
import { getDepartmentsById } from '../../services/api';

const DepartmentForm = () => {
    const { id } = useParams()
    const [formState, setFormState] = useState({})

    useEffect(() => {
        if (id) {
            getDepartmentsById(setFormState, id)
        }
    }, [id])

    const onChangeHandle = (e) => {
        setFormState({ ...formState, [e.target.name]: e.target.value })
    }

    useEffect(() => {
        console.log(formState);
    }, [formState])

    return (
        <>
            <Header heading={id ? "Редактировать Отдел" : "Добавить Отдел"} />
            <Box p={3}>
                <Grid2>
                    <TextField value={formState?.название_подразделения} onChange={onChangeHandle} name="название_подразделения" placeholder='Название отдела' />
                    <TextField value={formState?.фио_мол} onChange={onChangeHandle} name="фио_мол" placeholder='МОЛ' />
                    <TextField value={formState?.блок} onChange={onChangeHandle} name="блок" placeholder='Блок' />
                    <TextField value={formState?.корпус} onChange={onChangeHandle} name="корпус" placeholder='Корпус' />
                    <TextField value={formState?.этаж} onChange={onChangeHandle} name="этаж" placeholder='Этаж' />
                    <TextField value={formState?.номер_кабинета} onChange={onChangeHandle} name="номер_кабинета" placeholder='Номер кабинета' />
                    <TextField value={formState?.название_кабинета} onChange={onChangeHandle} name="название_кабинета" placeholder='Название кабинета' />
                </Grid2>
            </Box>
        </>
    );
};

export default DepartmentForm;
