import React, { useState } from 'react';
import Login from './pages/Login'; // Убедитесь, что путь правильный
import Register from './pages/Register'; // Убедитесь, что путь правильный
//import Main from './components/Main'; // Убедитесь, что путь правильный

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [showRegister, setShowRegister] = useState(false);

    return (
        <div>
            {!isLoggedIn ? (
                showRegister ? (
                    <Register onSwitchToLogin={() => setShowRegister(false)} />
                ) : (
                    <Login
                        onLogin={() => setIsLoggedIn(true)}
                        onSwitchToRegister={() => setShowRegister(true)}
                    />
                )
            ) : (
                <Main />
            )}
        </div>
    );
};

export default App;
