import React, { useState } from 'react';
import { login } from '../services/api';
import { Stack, TextField, Box, Button } from '@mui/material'; // Импортируем компоненты из Material-UI

const Login = ({ onLogin, onSwitchToRegister }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await login(username, password);
        if (response.data.success) {
            onLogin();
        } else {
            alert(response.data.message || 'Ошибка авторизации');
        }
    };

    return (
        <Stack height="100vh" justifyContent="center" alignItems="center">
            <Stack spacing="10px" padding="40px" borderRadius="8px" boxShadow='0px 1px 10px 5px rgba(34, 60, 80, 0.2)'>
                <Stack spacing="10px">
                    <TextField
                        style={{
                            width: "400px"
                        }}
                        type="text"
                        placeholder="Логин"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <TextField
                        style={{
                            width: "400px"
                        }}
                        type="password"
                        placeholder="Пароль"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </Stack>
                <Box display="flex" justifyContent="center">
                    <Button
                        style={{
                            width: "200px"
                        }}
                        variant="contained"
                        onClick={handleSubmit}
                    >
                        Войти
                    </Button>
                </Box>
                <Box display="flex" justifyContent="center">
                    <Button
                        style={{
                            width: "200px"
                        }}
                        variant="outlined"
                        onClick={onSwitchToRegister}
                    >
                        Зарегистрироваться
                    </Button>
                </Box>
            </Stack>
        </Stack>
    );
};

export default Login;
