import axios from 'axios';

const api = axios.create({
    baseURL: 'http://localhost/backend',
});

export const login = (username, password) => fetch('http://project.loc/login', {method: "POST", body: JSON.stringify({ username, password })}).then(res => res.json());
export const getDepartments = () => api.get('/index.php?action=getDepartments');
export const getMOLs = () => api.get('/index.php?action=getMOLs');
export const getEquipment = () => api.get('/index.php?action=getEquipment');
