import React, { useEffect, useState } from 'react';
import Header from '../../components/shared/Header';
import { Box, Grid2, TextField } from '@mui/material';
import { useParams } from 'react-router-dom';
import { getEquipmentById } from '../../services/api';

const EquipmentForm = () => {
    const { id } = useParams()
    const [formState, setFormState] = useState({})

    useEffect(() => {
        if (id) {
            getEquipmentById(setFormState, id)
        }
    }, [id])

    const onChangeHandle = (e) => {
        setFormState({ ...formState, [e.target.name]: e.target.value })
    }

    useEffect(() => {
        console.log(formState);
    }, [formState])

    return (
        <>
            <Header heading={id ? "Редактировать Оборудование" : "Добавить Оборудование"} />
            <Box p={3}>
                <Grid2>
                    <TextField onChange={onChangeHandle} value={formState?.фио_мол} name="фио_мол" placeholder='ФИО' />
                    <TextField onChange={onChangeHandle} value={formState?.должность} name="должность" placeholder='Должность' />
                    <TextField onChange={onChangeHandle} value={formState?.название_подразделения} name="название_подразделения" placeholder='Отдел' />
                </Grid2>
            </Box>
        </>
    );
};

export default EquipmentForm;
