import axios from 'axios';

const api = axios.create({
    baseURL: 'http://localhost/backend',
});


const __API_PATH = "http://project.loc/"

const fetchData = async (url, method = "GET", body = null) => {
    const options = {
        method,
    };

    if (body && method !== 'GET') {
        if (body instanceof FormData) {
            options.body = body;
        } else {
            options.body = JSON.stringify(body);
        }
    }

    let fullUrl = `${__API_PATH}${url}`;
    if (method === 'GET' && body) {
        const params = new URLSearchParams(body);
        fullUrl += `?${params.toString()}`;
    }

    const response = await fetch(fullUrl, options)
    return response.json();
};



export const login = async (username, password) => fetchData('login', "POST", { username, password });
export const getDepartments = async () => fetchData('get-departments');
export const getMOLs = async () => fetchData('get-mols');
export const getEquipment = async () => fetchData('get-equipment');
