import React, { useEffect, useState } from 'react';
import Header from '../../components/shared/Header';
import { Box, Grid2, TextField } from '@mui/material';
import { useParams } from 'react-router-dom';
import { getEquipmentById } from '../../services/api';

const EquipmentForm = () => {
    const { id } = useParams()
    const [formState, setFormState] = useState({})

    useEffect(() => {
        if (id) {
            getEquipmentById(setFormState, id)
        }
    }, [id])

    const onChangeHandle = (e) => {
        setFormState({ ...formState, [e.target.name]: e.target.value })
    }

    useEffect(() => {
        console.log(formState);
    }, [formState])

    return (
        <>
            <Header heading={id ? "Редактировать Оборудование" : "Добавить Оборудование"} />
            <Box p={3}>
                <Grid2>

                    <TextField value={formState.название_оборудования} onChange={onChangeHandle} name="название_оборудования" placeholder='название_оборудования' />
                    <TextField value={formState.название_подразделения} onChange={onChangeHandle} name="название_подразделения" placeholder='название_подразделения' />
                    <TextField value={formState.дата_производства} onChange={onChangeHandle} name="дата_производства" placeholder='дата_производства' />
                    <TextField value={formState.дата_поступления} onChange={onChangeHandle} name="дата_поступления" placeholder='дата_поступления' />
                    <TextField value={formState.дата_ввода_в_эксплуатацию} onChange={onChangeHandle} name="дата_ввода_в_эксплуатацию" placeholder='дата_ввода_в_эксплуатацию' />
                    <TextField value={formState.дата_вывода_из_эксплуатации} onChange={onChangeHandle} name="дата_вывода_из_эксплуатации" placeholder='дата_вывода_из_эксплуатации' />
                    <TextField value={formState.стоимость} onChange={onChangeHandle} name="стоимость" placeholder='стоимость' />
                    <TextField value={formState.заводской_номер} onChange={onChangeHandle} name="заводской_номер" placeholder='заводской_номер' />
                    <TextField value={formState.инвентарный_номер} onChange={onChangeHandle} name="инвентарный_номер" placeholder='инвентарный_номер' />
                </Grid2>
            </Box>
        </>
    );
};

export default EquipmentForm;
